import{d,c as r,a as e,t as n,e as p,k as m,o as l,_ as g,F as u,j as h,l as b}from"./index-DjTcSIdw.js";const v={class:"card-container"},C=["src"],y={class:"card-short-description"},T={class:"card-title"},f={class:"card-tags"},M={class:"card-description"},_=d({__name:"Card",props:{thumbnailImage:{},description:{},cardTitle:{},playerLevelCount:{},compaignMood:{}},setup(c){const a=c,i=`${a.playerLevelCount[0]} - ${a.playerLevelCount[1]}`,o=a.compaignMood.join(", ");return(s,t)=>(l(),r("div",v,[e("img",{src:a.thumbnailImage,alt:"image thumbnail",class:"card-image"},null,8,C),e("div",y,[e("h2",T,n(a.cardTitle),1),e("p",{class:"card-player-level"},[t[0]||(t[0]=e("strong",null,"Уровни игроков:",-1)),p(" "+n(i))]),e("p",f,[t[1]||(t[1]=e("strong",null,"Настроение:",-1)),p(" "+n(m(o)),1)]),e("p",M,n(a.description),1)])]))}}),I=g(_,[["__scopeId","data-v-60aeefd1"]]),L=[{cardTitle:"Lost Mine of Phandelver",thumbnailImage:"compaign-images/lost mine of phandelver.webp",description:"Классическое вступительное приключение, идеально подходящее для новых игроков и мастеров. Герои отправляются в небольшой регион Фаэруна, где исследуют заброшенные шахты, сталкиваются с бандитами и гоблинами, помогают местным жителям и постепенно раскрывают более крупный заговор. Кампания сочетает исследование, социальные сцены и базовый боевой опыт.",playerLevelCount:[1,5],compaignMood:["Приключение","Исследование","Лёгкий экшен"]},{cardTitle:"Hoard of the Dragon Queen",thumbnailImage:"compaign-images/hoard of the dragon queen.PNG",description:"Приключение начинается с масштабного нападения и быстро вовлекает героев в противостояние с культом Дракона. Игроки расследуют деятельность фанатиков, сопровождают караваны, проникают в логова врагов и постепенно узнают о планах по возрождению богини Тиамат. Кампания полна экшена, путешествий и нарастающего чувства угрозы.",playerLevelCount:[1,7],compaignMood:["Эпический","Героический","Тревожный"]},{cardTitle:"The Rise of Tiamat",thumbnailImage:"public/compaign-images/the rise of tiahmat.jpg",description:"Прямое продолжение истории о культе Дракона, где ставки становятся по-настоящему высокими. Герои взаимодействуют с могущественными фракциями, принимают стратегические решения и участвуют в судьбоносных сражениях. Кампания сочетает политические интриги, эпические битвы и ощущение надвигающегося апокалипсиса, ведущего к финальному столкновению с Тиамат.",playerLevelCount:[8,15],compaignMood:["Эпическая","Опасная","Напряжённая"]},{cardTitle:"Princes of the Apocalypse",thumbnailImage:"public/compaign-images/PrincesOfTheApocalypse.webp",description:"Мир стоит на пороге катастрофы: четыре элементальных культа стремятся уничтожить цивилизацию во имя стихий. Герои исследуют разрушенные поселения, древние руины и масштабные подземелья, постепенно раскрывая структуру врага. Кампания предлагает нелинейное прохождение, атмосферу надвигающегося конца света и множество сложных боевых столкновений.",playerLevelCount:[1,15],compaignMood:["Загадочная","Угрожающая","Апокалиптическая"]},{cardTitle:"Out of the Abyss",thumbnailImage:"public/compaign-images/out of the abyss.webp",description:"Мрачное и необычное приключение, начинающееся с плена в Подземье. Герои вынуждены выживать в мире безумия, демонов и постоянной угрозы. Кампания делает упор на психологическое давление, нехватку ресурсов и странные встречи. По мере развития истории игроки оказываются втянутыми в конфликт демонических принцев.",playerLevelCount:[1,15],compaignMood:["Тёмная","Хаотичная","Психологическая"]},{cardTitle:"Curse of Strahd",thumbnailImage:"public/compaign-images/curse-of-strahd_Digital_01.webp",description:"Готический хоррор в замкнутом мире Баровии, где над всем властвует древний вампир Страд фон Зарович. Кампания наполнена мрачной атмосферой, трагическими персонажами и моральными дилеммами. Игроки исследуют проклятые деревни, древние замки и сталкиваются с постоянным ощущением безысходности и наблюдения со стороны Страда.",playerLevelCount:[1,10],compaignMood:["Ужасы","Мрачная готика","Напряжение"]},{cardTitle:"Storm King’s Thunder",thumbnailImage:"public/compaign-images/Storm King’s Thunder.jpg",description:"Мир Фаэруна охвачен хаосом: гиганты выходят из-под контроля и угрожают королевствам. Герои путешествуют по огромным регионам, заключают союзы и раскрывают причины конфликта. Кампания масштабная и эпическая, с упором на исследования, крупные сражения и постепенное вовлечение в дела богов и титанов.",playerLevelCount:[1,11],compaignMood:["Эпический","Героический","Эпическая борьба"]},{cardTitle:"Tomb of Annihilation",thumbnailImage:"public/compaign-images/Tomb of Annihilation-cut.jpg",description:"Опасное приключение в диких джунглях Чульта, где над миром нависло проклятие смерти. Кампания сочетает исследование, выживание, смертельно опасные ловушки и один из самых известных данжей в истории D&D. Атмосфера постоянной угрозы и неизвестности делает каждое решение игроков критически важным.",playerLevelCount:[1,11],compaignMood:["Пугающая","Напряжённая","Survival horror"]},{cardTitle:"Waterdeep: Dragon Heist",thumbnailImage:"public/compaign-images/Waterdeep Dragon Heist.jpg",description:"Городская кампания, сосредоточенная на интригах, расследованиях и социальном взаимодействии. Водяная Глубина становится живым персонажем истории, полной тайных обществ, преступных синдикатов и политических игр. Кампания почти не про классические подземелья, делая акцент на диалогах, выборе союзников и последствиях решений.",playerLevelCount:[1,5],compaignMood:["Урбанистика","Детектив","Социальная интрига"]},{cardTitle:"Waterdeep: Dungeon of the Mad Mage",thumbnailImage:"public/compaign-images/Waterdeep Dungeon of the Mad Mage.jpg",description:"Гигантский мегаданж, расположенный под Водяной Глубиной. Игроки исследуют десятки уровней Ундермаунтена, каждый со своей темой, врагами и тайнами. Кампания рассчитана на любителей подземелий, тактических боёв и долгосрочного прогресса персонажей, с минимальным акцентом на социальные сцены.",playerLevelCount:[5,20],compaignMood:["Мегаданж","Исследование","Приключение"]},{cardTitle:"Baldur’s Gate: Descent into Avernus",thumbnailImage:"public/compaign-images/Baldur’s Gate Descent into Avernus.jpg",description:"История начинается в Балдур’s Gate и постепенно приводит героев в первый круг ада — Авернус. Кампания мрачная и драматичная, с темами сделок с дьяволами, морального выбора и отчаяния. Игроки управляют адскими машинами, сталкиваются с демонами и решают судьбы целых городов.",playerLevelCount:[1,13],compaignMood:["Адский","Драматический","Эпический"]},{cardTitle:"Icewind Dale: Rime of the Frostmaiden",thumbnailImage:"public/compaign-images/Icewind Dale Rime of the Frostmaiden.webp",description:"Суровое приключение в землях вечной зимы, где холод — такой же враг, как и монстры. Кампания делает упор на выживание, изоляцию и постепенное раскрытие древних тайн. Атмосфера безысходности и одиночества усиливается нелинейной структурой и множеством побочных историй.",playerLevelCount:[1,12],compaignMood:["Мрачный","Survival","Холодная изоляция"]},{cardTitle:"Tales from the Yawning Portal",thumbnailImage:"public/compaign-images/Tales from the Yawning Portal.jpg",description:"Сборник классических подземелий, адаптированных под пятую редакцию. Каждое приключение можно использовать отдельно или связать в общую кампанию. Это отличный выбор для мастеров, желающих познакомить игроков с культовыми данжами D&D, сфокусировавшись на исследовании, ловушках и тактических боях.",playerLevelCount:[1,15],compaignMood:["Разнообразная","Приключения","Классические данжи"]},{cardTitle:"Dragon of Icespire Peak",thumbnailImage:"public/compaign-images/Dragon of Icespire Peak-cut.jpg",description:"Дружелюбная кампания для новичков с модульной структурой заданий. Герои берут контракты, защищают поселения и постепенно сталкиваются с угрозой ледяного дракона. Кампания проста в освоении, легко масштабируется и отлично подходит для первых шагов в D&D.",playerLevelCount:[1,6],compaignMood:["Приключение","Лёгкая","Дружелюбная"]},{cardTitle:"Ghosts of Saltmarsh",thumbnailImage:"public/compaign-images/Ghosts of Saltmarsh.jpg",description:"Морские приключения, вдохновлённые классическими модулями. Игроки исследуют прибрежные деревни, острова и затонувшие корабли, сталкиваясь с пиратами и древними тайнами. Кампания сочетает морскую романтику, исследование и элементы хоррора, предлагая гибкую структуру для мастера.",playerLevelCount:[1,12],compaignMood:["Морская","Исследовательская","Таинственная"]},{cardTitle:"Critical Role: Call of the Netherdeep",thumbnailImage:"public/compaign-images/Critical Role Call of the Netherdeep-cut.jpg",description:"Эпическая история, происходящая в мире Exandria. Кампания делает сильный акцент на персонажах, их мотивации и эмоциональных конфликтах. Игроки участвуют в гонке за древней силой, принимают сложные решения и сталкиваются с последствиями своих выборов в насыщенном лором мире.",playerLevelCount:[3,12],compaignMood:["Эпическая","Мистическая","Драматическая"]},{cardTitle:"Candlekeep Mysteries",thumbnailImage:"public/compaign-images/Candlekeep Mysteries-cut.jpeg",description:"Сборник коротких приключений, объединённых легендарной библиотекой Candlekeep. Каждая история начинается с загадочной книги и предлагает уникальный сюжет. Кампания идеально подходит для эпизодической игры, сочетая детектив, мистику и разнообразные жанры в компактных сценариях.",playerLevelCount:[1,16],compaignMood:["Детектив","Мистический","Короткие сценарии"]},{cardTitle:"Journeys through the Radiant Citadel",thumbnailImage:"public/compaign-images/Journeys through the Radiant Citadel-cut.jpg",description:"Антология приключений, вдохновлённых разными культурами и мифами. Герои путешествуют через сияющую Цитадель в уникальные миры, каждый со своей атмосферой и проблемами. Кампания делает упор на разнообразие, социальные конфликты и сказочное приключенческое настроение.",playerLevelCount:[3,10],compaignMood:["Приключения","Сказочный","Разнообразный"]},{cardTitle:"Keys from the Golden Vault",thumbnailImage:"public/compaign-images/Keys from the Golden Vault-cut.jpg",description:"Серия приключений в жанре ограблений. Игроки планируют сложные операции, проникают в охраняемые места и решают головоломки. Кампания поощряет нестандартное мышление, скрытность и командную работу, предлагая альтернативу классическому «бой-данж-лут» стилю.",playerLevelCount:[5,12],compaignMood:["Интеллектуальная","Heist","Приключение"]},{cardTitle:"Phandelver and Below: The Shattered Obelisk",thumbnailImage:"public/compaign-images/Phandelver and Below The Shattered Obelisk.jpeg",description:"Продолжение истории Фандалина, расширяющее события Lost Mine of Phandelver. Кампания добавляет элементы космического ужаса и более глубокие подземелья. Знакомые места обретают новую угрозу, а приключение становится мрачнее и сложнее, сохраняя дух исследования и героизма.",playerLevelCount:[4,10],compaignMood:["Исследование","Приключение","Данж"]}],D={class:"compaigh-container"},j={class:"compaigh-card-group"},P=d({__name:"CompanyPage",setup(c){return(a,i)=>(l(),r("section",D,[i[0]||(i[0]=e("h1",{class:"compaigh-title"},"DnD : Официальные компании",-1)),e("div",j,[(l(!0),r(u,null,h(m(L),(o,s)=>(l(),b(I,{key:s,thumbnailImage:o.thumbnailImage,description:o.description,cardTitle:o.cardTitle,playerLevelCount:o.playerLevelCount,compaignMood:o.compaignMood},null,8,["thumbnailImage","description","cardTitle","playerLevelCount","compaignMood"]))),128))])]))}});export{P as default};
